import React, { useState } from 'react'
import { Alert, Card, Image, Tooltip, Skeleton, notification } from 'antd'
import { FileSearchOutlined, SendOutlined, ShoppingCartOutlined } from '@ant-design/icons'
import { useMoralis, useNFTBalances } from 'react-moralis'
import { getExplorer } from '../../../utils/networks'

import { Modal } from './modal'

const { Meta } = Card

const NFT: React.FC = () => {
  const { data: NFTBalances } = useNFTBalances()

  const { error, isFetching } = useNFTBalances()

  const { chainId } = useMoralis()

  const viewOnBlockExplorer = (_chainId, token_address) => {
    if (_chainId) window.open(`${getExplorer(_chainId)}address/${token_address}`, '_blank')
    return undefined
  }

  const [visible, setVisibility] = useState(false)
  const [warning, setWarning] = useState<string | null>(null)
  const [address, setAddress] = useState<string>('')

  const onCancel = () => {
    setWarning(null)
    setAddress('')
    setVisibility(false)
  }

  const openNotification = placement => {
    notification.info({
      message: 'OPENSEA MARKET',
      description: 'OpenSea integration coming soon!',
      placement,
    })
  }

  return (
    <div style={{ padding: '15px', maxWidth: '1030px', width: '100%' }}>
      <h1>NFT Balances</h1>
      {error && <Alert message="Could not fetch NFTs!" type="error" />}
      <div
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          WebkitBoxPack: 'start',
          justifyContent: 'flex-start',
          margin: '0 auto',
          maxWidth: '1000px',
          width: '100%',
          gap: '10px',
        }}
      >
        <Skeleton loading={isFetching}>
          {NFTBalances?.result &&
            NFTBalances.result.map(nft => {
              // nft = verifyMetadata(nft)
              return (
                <Card
                  hoverable
                  actions={[
                    <Tooltip title="View On Blockexplorer">
                      <FileSearchOutlined
                        style={{ color: '#61F38E' }}
                        onClick={() => viewOnBlockExplorer(chainId, nft.token_address)}
                      />
                    </Tooltip>,
                    <Tooltip title="Transfer NFT">
                      <SendOutlined
                        style={{ color: '#61F38E' }}
                        onClick={() => setVisibility(true)}
                      />
                    </Tooltip>,
                    <Tooltip title="Sell On OpenSea">
                      <ShoppingCartOutlined
                        style={{ color: '#61F38E' }}
                        onClick={() => openNotification('topLeft')}
                      />
                    </Tooltip>,
                  ]}
                  style={{ width: 240, border: '2px solid #e7eaf3' }}
                  cover={
                    <Image
                      preview={false}
                      src={nft?.image || 'error'}
                      fallback="data:image/png;base64,"
                      alt=""
                      style={{ height: '300px' }}
                    />
                  }
                  key={nft.token_id}
                >
                  <Meta title={nft.name} description={nft.token_address} />
                </Card>
              )
            })}
        </Skeleton>
        <Modal
          visible={visible}
          onCancel={onCancel}
          warning={warning}
          setWarning={setWarning}
          address={address}
          setAddress={setAddress}
        />
      </div>
    </div>
  )
}

export default NFT
